import React , {useEffect, useState} from 'react';
import Axios from 'axios';
import {CustomerNavBar} from "./CustomerNavBar";

export const Customer = ()=> {

    //Required initialization

    const [userId, setUserId] = useState(0);
    const [cust_name, setCust_name] = useState('');
    const [cust_mobno, setCust_mobno] = useState('');
    const [cust_addr, setCust_addr] = useState('');
    
    Axios.defaults.withCredentials = true;

    
    //Required methods
    useEffect(()=> {
        Axios.get("http://localhost:3005/login").then((response) => {
            console.log("after cookie");
            if(response.data.loggedIn){
                console.log(response);
                setUserId(response.data.user[0].cid);
            }
        });
    }, []);

    
    
    
    const showVeh = ()=>{
        Axios.post("http://localhost:3005/customerDetails",
        {
            id : userId
        }).then((response)=> {
            setCust_name(response.data.customers[0].name);
            setCust_addr(response.data.customers[0].addr);
            setCust_mobno(response.data.customers[0].mobno);
            console.log(response);
            console.log("got info");
        });
    };
    

    return (
        <div>
            <h1>Welcome {username} !!</h1>
            <button onClick = {showCustInfo}>My Profile</button>
            <h2>{cust_name} {cust_mobno} {cust_addr}</h2>
            {<CustomerNavBar/>}
            <hr></hr>
            
            
            
        </div>
    );
}
