import React , {useEffect, useState} from 'react';
import Axios from 'axios';
import {CustomerNavBar} from "./CustomerNavBar";

export const Customer = ()=> {

    //Required initialization

    const [userId, setUserId] = useState(0);
    const [userName, setuserName] = useState('');
    const [cust_name, setCust_name] = useState('');
    const [cust_mobno, setCust_mobno] = useState('');
    const [cust_addr, setCust_addr] = useState('');

    const [vehId, setVehId] = useState(0);
    const [td_date, set_td_date] = useState(null);
    const [td_slot, set_td_slot] = useState('');
    
    Axios.defaults.withCredentials = true;

    
    //Required methods
    useEffect(()=> {
        Axios.get("http://localhost:3005/login").then((response) => {
            console.log("after cookie");
            if(response.data.loggedIn){
                console.log(response);
                setUserId(response.data.user[0].cid);
                setuserName(response.data.user[0].username);
            }
        });
    }, []);

    
    
    
    const showCustInfo = ()=>{
        Axios.post("http://localhost:3005/customerDetails",
        {
            id : userId
        }).then((response)=> {
            setCust_name(response.data[0].name);
            setCust_addr(response.data[0].addr);
            setCust_mobno(response.data[0].mobno);
        });
    };
    

    return (
        <div>
            <h1>Welcome {userName} !!</h1>
            <button onClick = {showCustInfo}>My Profile</button>
            <h2>{cust_name} {cust_mobno} {cust_addr}</h2>
            {<CustomerNavBar/>}
            <hr></hr>
            <h1>Book  your Test Drive</h1>

            <div>

                <label>"Date"</label>
                <input type = "date"  
                onChange={(event)=> {
                    set_td_date(event.target.value);
                    }}
                />

                <label>"Slot(Timmings)"</label>
                <select
                    value={td_slot}
                    onChange={(event) =>{
                        set_td_slot(event.target.value);
                    }}
                >
                    <option values = "">select time slot</option>
                    <option values = "10:11">10:00 am: 11:00 am</option>
                </select>

                </div>
            
            
        </div>
    );
}
