import React , {useEffect, useState} from 'react';
import { Navigate } from "react-router-dom";
import Axios from 'axios';
import { Customer } from '../components/Customer';
import { Manager } from '../components/Manager';

export const Home = ()=> {
    
    const [userRole, setUserRole] = useState('');
    const [userName, setuserName] = useState('');

    const [cust_name, set_cust_name] = useState('');
     const [cust_addr, set_cust_addr] = useState('');
     const [cust_mobno, set_cust_mobno] = useState('');
    const [userId, setuserId] = useState(0);
    
    Axios.defaults.withCredentials = true;

    useEffect(()=> {
        Axios.get("http://localhost:3005/login").then((response) => {
            console.log("after cookie");
            if(response.data.loggedIn){
                console.log(response);
                setUserRole(response.data.user[0].role);
                setuserName(response.data.user[0].username);
                setuserId(response.data.user[0].cid);
            }
            else{
                setUserRole('NA');
            }
        });
    }, []);

    const logout = ()=> {
        Axios.get('http://localhost:3005/logout').then( (response)=> {
          setUserRole('NA');
        });
    }

    const profile = ()=>{
        Axios.post("http://localhost:3005/customerDetails",{
            id : userId
        }).then((response)=> {  
            set_cust_name(response.data[0].name);
            set_cust_addr(response.data[0].addr);
            set_cust_mobno(response.data[0].mobno);
            console.log(response);
        });
    };

    
    if(userRole == 'NA'){
        return <Navigate to = "/"/>
      }

    


    return (
        <div>
            <h1>Welcome {userName} !!</h1>
            <button onClick={logout}>LogOut</button>

            {userRole == 'c' && <Customer/>}
            {userRole == 'm' && <Manager/>}
            

        </div>
    );
}
