import React , {useEffect, useState} from 'react';
import Axios from 'axios';
import {CustomerNavBar} from "./CustomerNavBar";

export const Customer = ()=> {

    //Required initialization

    const [userId, setUserId] = useState(0);
    const [cust_name, setCust_name] = useState('');
    const [cust_mobno, setCust_mobno] = useState('');
    const [cust_addr, setCust_addr] = useState('');
    
    Axios.defaults.withCredentials = true;

    
    //Required methods
    useEffect(()=> {
        Axios.get("http://localhost:3005/login").then((response) => {
            console.log("after cookie");
            if(response.data.loggedIn){
                console.log(response);
                setUserId(response.data.user[0].cid);
            }
        });
    }, []);

    useEffect(()=> {
        if(userId != 0){
            Axios.get("http://localhost:3005/customerDetails",
            { id : userId
            }).then((response) => {
                setCust_name(response.data[0].name);
                setCust_addr(response.data[0].addr);
                setCust_mobno(response.data[0].mobno);
                console.log(response);
            });
        }
        else{
            alert("Not logged In");
        }
    }, []);


    return (
        <div>
            <h1>Customer</h1>
            {<CustomerNavBar/>}
            <hr></hr>
            <h2>Hello {cust_name} !!</h2>
            <label>Mobno</label>{cust_mobno}
            <label>Address</label>{cust_addr}
        </div>
    );
}
