import React , {useEffect, useState} from 'react';
import Axios from 'axios';
import {CustomerNavBar} from "../components/CustomerNavBar";

export const TrackTestDrive = ()=> {
    //Initialization
    const [userId, setUserId] = useState(0);
    const [userName, setuserName] = useState('');

    const [history, setHistory] = useState([]);
    const [active, setActive] = useState([]);

    Axios.defaults.withCredentials = true;

    
    //Required methods
    useEffect(()=> {
        Axios.get("http://localhost:3005/login").then((response) => {
            console.log("after cookie");
            if(response.data.loggedIn){
                console.log(response);
                //setUserId(response.data.user[0].cid);
                setuserName(response.data.user[0].username);
            }
        });
    }, []);

    const showHistory = ()=>{
        Axios.post("http://localhost:3005/history",{
            id : userId
        }).then((response)=> {
            setHistory(response.data);
        });
    };

    //render page content

    return (
        <div>
            <h1>Hello {userName}</h1>
            {<CustomerNavBar/>}
            <hr></hr>
            
            <div>
                <h2>Track customer testdrive</h2>
                    <button onClick = {showHistory}>Show Past Testdrives</button>
                        {history.map( (val, key)=> {
                        return <div className='history-td'>
                                    <h2>{val.model}</h2>
                                    <h2>{val.type}</h2>
                                    <h2>{val.td_date}</h2>
                                    <h2>{val.slot_name}</h2>
                                    <h2>{val.exe_name}</h2>
                                    <h2>{val.mobno}</h2>
                                </div>
                                
                        })}
            </div>
            
        </div>
    );
}
