import React , {useEffect, useState} from 'react';
import Axios from 'axios';
import {CustomerNavBar} from "../components/CustomerNavBar";



export const TrackTestDrive = ()=> {
    //Initialization
    const [userId, setUserId] = useState(0);
    const [userName, setuserName] = useState('');

    const [history, setHistory] = useState([]);
    const [active, setActive] = useState([]);

    Axios.defaults.withCredentials = true;

    
    //Required methods
    useEffect(()=> {
        Axios.get("http://localhost:3005/login").then((response) => {
            console.log("after cookie");
            if(response.data.loggedIn){
                console.log(response);
                setUserId(response.data.user[0].cid);
                setuserName(response.data.user[0].username);
            }
        });
    }, []);

    const showHistory = ()=>{
        Axios.post("http://localhost:3005/history",{
            id : userId
        }).then((response)=> {
            if(response.data.length > 0){
                setHistory(response.data);
                console.log(response);
            }
            else{
                alert('Currently there are no past testdrives');
            }
        });
    };

    const track_td = ()=>{
        Axios.post("http://localhost:3005/track",{
            id : userId
        }).then((response)=> {
            if(response.data.length > 0){
            setActive(response.data);
            console.log(response);
            }
            else{
                alert('No active testdrives available');
            }
        });
    };

    const Cancel = (id)=> {
        Axios.delete(`http://localhost:3005/cancel/${id}`).then((response)=> {
            if(response.data[0].cancelStatus){
                alert('Cancelled successfully');
            }
            else{
                alert('Cancel unsuccessful');
            }
        });
    };


    //Columns

    const column = [
        {
            name : 'model',
            selector : (row) => row.model
        },
        {
            name : 'type',
            selector : (row) => row.type
        },
        {
            name : 'td_date',
            selector : (row) => row.td_date
        },
        {
            name : 'slot_name',
            selector : (row) => row.slot_name
        },
        {
            name : 'exe_name',
            selector : (row) => row.exe_name
        }
        ,
        {
            name : 'mobno',
            selector : (row) => row.mobno
        }

    ];



    //render page content

    return (
        <div>
            <h1>Hello {userName}</h1>
            {<CustomerNavBar/>}
            <hr></hr>
            <button onClick = {track_td}>Track Active Testdrives</button> <button onClick = {showHistory}>Show Past Testdrives</button>
            <div>
                <h2>Track active testdrive</h2>
                        {active.map( (val)=> {
                        return <div className='active-td'>
                                    <label>Vehicle Model</label><h2>{val.model}</h2>
                                    <label>Transmission Type</label><h2>
                                        {val.type == 'a' && "Automatic"}
                                        {val.type == 'm' && "Manual"}
                                    </h2>
                                    <label>Booking Date</label><h2>{val.td_date}</h2>
                                    <label>Time Slot</label><h2>{val.td_slot}</h2>
                                    <label>Executive Name</label><h2>{val.exename}</h2>
                                    <label>Executive Mob No</label><h2>{val.exemobno}</h2>
                                    <label>Current Status</label><h2>
                                        {val.status == 'p' && "Pending"}
                                        {val.status == 'c' && "Confirmed"}
                                    </h2>
                                    <button onClick = {()=> {Cancel(val.tid)}} >Cancel Testdrive</button>
                                    <hr></hr>
                                </div>
                                
                        })}
            </div>

            <hr></hr><hr></hr>

            <HistoryTable/>
            
        </div>
    );
}
