import React , {useEffect, useState} from 'react';
import Axios from 'axios';
import {ManagerNavBar} from "../components/ManagerNavBar";

export const Administrate = ()=> {

    //Required functions and variables

    const [exeName, setExeName] = useState("");
    const [exeMobNo, setExeMobNo] = useState("");
    const [vehModel, setvehModel] = useState("");
    const [vehType, setvehType] = useState("");

    const [exeList, setExeList] = useState([]);
    const [vehList, setVehList] = useState([]);

    //Executive CRUD operations

    const addExe = ()=> {
        if(exeName.length > 0 && exeMobNo.length == 10){
        Axios.post("http://localhost:3005/addExe", 
        {exeName : exeName, exeMobNo : exeMobNo
        }).then(() => {
            setExeList([
            ...exeList,
            {
                exename : exeName,
                exemobno : exeMobNo
            }
            ]);
        });
        }
        else{
            alert("Invalid data");
        }
    };

    const showExes = ()=>{
        Axios.get("http://localhost:3005/exeDetails").then((response)=> {
            setExeList(response.data);
            console.log(exeList);
        });
    };

    const updexe = (id)=>{
        if(exeName.length > 0 && exeMobNo.length == 10){
        Axios.put("http://localhost:3005/updateExe", 
        {exeName : exeName, exeMobNo : exeMobNo, exeId : id
        }).then((response)=> {
            setExeList(exeList.map((val)=>{
                if(val.eid == id){
                    return {eid : id, exename : exeName, exemobno : exeMobNo};
                }
                else {
                    return val;
                }
            }));
            alert("update success");
            }
        );
        }
        else{
            alert("Enter vehicle model and type");
        }
    };

    const delexe = (id)=> {
        Axios.delete(`http://localhost:3005/deleteExe/${id}`).then((response)=> {
            setExeList(
            exeList.filter((val)=> {
            if(val.id != id){
                return val;
            }
            })
            );
            alert('Delete successfully');
        });
    };
    //--------------------------------------------------------------------------------------------


    //Vehicle CRUD operations

    const addVeh = ()=> {
        if(vehModel.length > 0 && vehType.length == 1){
        Axios.post("http://localhost:3005/addVeh", 
        {name : vehModel, type : vehType
        }).then((response) => {
            if(response.status)
                setVehList([
                ...vehList,
                {
                    model : vehModel,
                    type : vehType
                }
                ]);
        });
        }
        else{
            alert("Enter vehicle model and type");
        }
    };

    const showVeh = ()=>{
        Axios.get("http://localhost:3005/vehDetails").then((response)=> {
            setVehList(response.data);
        });
    };

    const updVeh = (id)=>{
        if(vehModel.length > 0 && vehType.length == 1){
            Axios.put("http://localhost:3005/updateVeh", 
            {name : vehModel, type : vehType, id : id
            }).then((response)=> {
                setVehList(vehList.map((val)=>{
                    if(val.vehid == id){
                        return {vehid : id, model : vehModel, type : vehType};
                    }
                    else {
                        return val;
                    }
                }));
                alert("update success");
                }
        );
        }
        else{
            alert("Enter vehicle model and type");
        }
    };

    const delVeh = (id)=> {
        Axios.delete(`http://localhost:3005/deleteVeh/${id}`).then((response)=> {
            setVehList(
                response.data.filter((val)=> {
            if(val.id != id){
                return val;
            }
            })
            );
            alert('Delete successfully');
        });
    };

    //------------------------------------------------------------------------------------------

    //Render page content
    return (
        <div>
            
            <div>
                <h1>Welcome Manager!!</h1>
                {<ManagerNavBar/>}
                <hr></hr>
            </div>

    
            <div>
                <h2>Manage Executives</h2>
                <div>
                    <label>Executive Name</label>
                    <input 
                        type = "text"
                        onChange = {(event)=> {
                        setExeName(event.target.value);
                        }}
                    />
                    <label>Mobile No</label>
                    <input 
                        type = "text"
                        onChange = {(event)=> {
                        setExeMobNo(event.target.value);
                        }}
                    />
                    <button onClick = {addExe}>Add executive</button>
                    <hr></hr>
                </div>

                <div>
                    <button onClick = {showExes}>Show executives</button>
                        {exeList.map( (val, key)=> {
                        return <div className='executives'>
                                    <label>Executive Name</label><h2>{val.exename}</h2>
                                    <label>Executive Mob No</label><h2>{val.exemobno}</h2>
                                    <button onClick = {()=> {updexe(val.eid)}} >Update this executive</button>  
                                    <button onClick = {()=> {delexe(val.eid)}} >Delete this executive</button>
                                </div>
                                
                        })}
                </div>
            </div>

            <hr></hr>
            <hr></hr>


            <div>
                <h2>Manage Vehicle Models</h2>
                <div>
                    <label>Model Name</label>
                    <input 
                        type = "text"
                        onChange = {(event)=> {
                        setvehModel(event.target.value);
                        }}
                    />
                    <label>Vehicle Type (a/m)</label>
                    <input 
                        type = "text"
                        onChange = {(event)=> {
                        setvehType(event.target.value);
                        }}
                    />
                    <button onClick = {addVeh}>Add Model</button>
                    <hr></hr>
                </div>

                <div>
                    <button onClick = {showVeh}>Show Vehicle Models</button>
                        {vehList.map( (val, key)=> {
                        return <div className='vehicles'>
                                    <label>Model</label><h2>{val.model}</h2>
                                    <label>Transmission Type</label><h2>
                                        {val.type == 'a' && "Automatic"}
                                        {val.type == 'm' && "Manual"}
                                    </h2>
                                    <button onClick = {()=> {updVeh(val.vehid)}} >Update this Model</button>  
                                    <button onClick = {()=> {delVeh(val.vehid)}} >Delete this Model</button>
                                </div>
                                
                        })}
                </div>
            </div>

        </div>
    );
}
