import React , {useEffect, useState} from 'react';
import Axios from 'axios';
import {ManagerNavBar} from "./ManagerNavBar";


export const Manager = ()=> {

    //Required functions and variables
    const [testDriveList, setTestDriveList] = useState([]);


    //On page refresh
    /*useEffect(()=>{
        Axios.get("http://localhost:3005/confirmed").then((response) => {
          setTestDriveList(response);
        });
    }, [])*/

    //Confirmed testdrives functions-----------------------------------------

    const showConfirmedTD = ()=>{
        Axios.get("http://localhost:3005/viewConfirmed").then((response)=> {
            if(response.data.length > 0){
                console.log(response);
                setTestDriveList(response.data);
            }
            else{
                alert('No confirmed testdrives available');
            }
        });
    };

    const doneTestDrive = (id)=>{
        Axios.put("http://localhost:3005/done", 
        { tid : id
        }).then((response)=> {
                if(response.data.doneStatus == "false"){
                    alert("done failed!!");
                }
                else{
                    alert("Testdrive closed successfully!!");
                }
            }
        );
    };

    //Columns

    const column_legacy = [
        {
            name : 'tid',
            selector : (row) => row.tid
        },
        {
            name : 'name',
            selector : (row) => row.name
        },
        {
            name : 'addr',
            selector : (row) => row.addr
        },
        {
            name : 'cust_mobno',
            selector : (row) => row.mobno
        },
        {
            name : 'td_date',
            selector : (row) => row.td_date
        },
        {
            name : 'slot_name',
            selector : (row) => row.td_slot
        },
        {
            name : 'model',
            selector : (row) => row.model
        },
        {
            name : 'type',
            selector : (row) => row.type
        },
        {
            name : 'exename',
            selector : (row) => row.exename
        },
        {
            name : 'exemobno',
            selector : (row) => row.exemobno
        },
        {
            name : 'Close',
            cell : (row)=> (
                <button onClick={()=> {doneTestDrive(row.tid)}}>Done</button>
            )
        }
    ];

    //-----------------------------------------------------------------------

    return (
        <div>
            <h1>Manager</h1>
            {<ManagerNavBar/>}
            
            <hr></hr>
            <h2>Confirmed Test-Drives</h2>
            <button onClick = {showConfirmedTD} >Show Confirmed Testdrives</button>
            {testDriveList.map( (val, key)=> {
                        return <div className='vehicles'>
                                    <label>Customer Name</label><h2>{val.name}</h2>
                                    <label>Customer Address</label><h2>{val.addr}</h2>
                                    <label>Customer Mob No</label><h2>{val.mobno}</h2>
                                    <label>Booking Date</label><h2>{val.td_date}</h2>
                                    <label>Time Slot</label><h2>{val.td_slot}</h2>
                                    <label>Vehicle Model</label><h2>{val.model}</h2>
                                    <label>Transmission Type</label><h2>
                                        {val.type == 'a' && "Automatic"}
                                        {val.type == 'm' && "Manual"}
                                    </h2>
                                    <label>Executive Name</label><h2>{val.exename}</h2>
                                    <label>Executive Mob No</label><h2>{val.exemobno}</h2>
                                    <button onClick = {()=> {doneTestDrive(val.tid)}} >Done</button>  
                                </div>
                                
            })}

        </div>
    );
}
