import React , {useEffect, useState} from 'react';
import Axios from 'axios';
import {ManagerNavBar} from "../components/ManagerNavBar";

export const Pending = ()=> {

    //Required initialization
    const [pending, setPending] = useState([]); 
    const [exeId, setexeId] = useState(0);
    
    //required methods

    const showPending = ()=>{
        Axios.put("http://localhost:3005/pending" ).then((response)=> {
            setPending(response.data);
            console.log(response);
        });
    };

    const Confirm = (id)=>{
        if(exeId.length > 0){
        Axios.put("http://localhost:3005/confirm", 
        {name : vehModel, type : vehType, id : id
        }).then((response)=> {
            setVehList(vehList.map((val)=>{
                if(val.vehid == id){
                    return {vehid : id, model : vehModel, type : vehType};
                }
                else {
                    return val;
                }
            }));
            alert("update success");
            }
        );
        }
        else{
        alert("Invalid data");
        }
    };


    return (
        <div>
            <h1>Manager</h1>
            {<ManagerNavBar/>}
            <hr></hr>
            <h2>Manager Pending specific</h2>
            <div>
                <h2>Pending Test Drives</h2>
                <button onClick = {()=> {showPending(val.tid)}} >Pending Testdrive</button>
                        {pending.map( (val)=> {
                        return <div className='active-td'>
                                    <h2>{val.model}</h2>
                                    <h2>{val.type}</h2>
                                    <h2>{val.td_date}</h2>
                                    <h2>{val.td_slot}</h2>
                                    <h2>{val.exename}</h2>
                                    <h2>{val.exemobno}</h2>
                                    <h2>{val.status}</h2>
                                    <label>Executive ID</label>
                                    <input 
                                        type = "text"
                                        onChange = {(event)=> {
                                        setexeId(event.target.value);
                                        }}
                                    />
                                    <button onClick = {()=> {Confirm(val.tid)}} >Cancel Testdrive</button>
                                    <hr></hr>
                                </div>
                                
                        })}
            </div>
        </div>
    );
}
