import React , {useEffect, useState} from 'react';
import Axios from 'axios';
import {ManagerNavBar} from "../components/ManagerNavBar";

export const Pending = ()=> {

    //Required initialization
    const [pending, setPending] = useState([]); 
    const [availExeList, setAvailExeList] = useState([]); 
    const [exeId, setexeId] = useState(0);
    const [searchId, setSearchId] = useState(0);
    const [td_date, set_td_date] = useState('');
    const [td_slot, set_td_slot] = useState('');
    const [vehAvailStatus, setVehAvailStatus] = useState('');
    
    //required methods

    const showPending = ()=>{
        Axios.get("http://localhost:3005/viewPending" ).then((response)=> {
            setPending(response.data);
            console.log(response);
        });
    };

    const Confirm = (id)=>{
        if(exeId.length > 0){
        Axios.put("http://localhost:3005/confirm", 
        { tid : id,
          eid : exeId
        }).then((response)=> {
            alert("confirmed successfully");
            }
        );
        }
        else{
        alert("Invalid data");
        }
    };

    const availExe = ()=>{
        Axios.post("http://localhost:3005/availExe",{
            id : searchId,
            date : td_date,
            slot : td_slot
        }).then((response)=> {
            setAvailExeList(response.data);
            console.log(response);
        });
    };

    const vehAvailCheck = ()=>{
        Axios.post("http://localhost:3005/vehAvailable",{
            id : userId
        }).then((response)=> {
            setActive(response.data);
            console.log(response);
        });
    };

    //Render page content
    return (
        <div>
            <h1>Manager</h1>
            {<ManagerNavBar/>}
            <hr></hr>
            <h2>Manage Pending testdrives</h2>
            <div>
                <div>
                    <label>"ID"</label>
                        <input 
                            type = "text"
                            onChange = {(event)=> {
                            setSearchId(event.target.value);
                        }}
                    />
                    <label>"Date"</label>
                    <input 
                        type = "text"
                        onChange = {(event)=> {
                        set_td_date(event.target.value);
                        }}
                    />
                    <label>"Slot(Timmings)"</label>
                    <input 
                        type = "text"
                        onChange = {(event)=> {
                        set_td_slot(event.target.value);
                        }}
                    />
                </div>
                <div>
                    <h2>Vehicle Availability</h2>
                    <button onClick = {vehAvailCheck} >Check Vehicle Availability</button>
                    <hr></hr>
                    <hr></hr>
                </div>

                <div>
                    <h2>Executive Availability</h2>
                    <button onClick = {availExe} >Show Available Executives</button>
                    <hr></hr>
                    <hr></hr>
                </div>

                <div>
                <button onClick = {showPending} >Pending Testdrive</button>
                        {pending.map( (val)=> {
                        return <div className='active-td'>
                                    <h2>{val.name}</h2>
                                    <h2>{val.addr}</h2>
                                    <h2>{val.mobno}</h2>
                                    <h2>{val.vehid}</h2>
                                    <h2>{val.td_date}</h2>
                                    <h2>{val.td_slot}</h2>
                                    <label>Executive ID</label>
                                    <input 
                                        type = "text"
                                        onChange = {(event)=> {
                                        setexeId(event.target.value);
                                        }}
                                    />
                                    <button onClick = {()=> {Confirm(val.tid)}} >Confirm Testdrive</button>
                                    <hr></hr>
                                </div>
                        })}
                </div>
            </div>    
        </div>
    );
}
