import React , {useEffect, useState} from 'react';
import Axios from 'axios';
import {ManagerNavBar} from "./ManagerNavBar";


export const Manager = ()=> {

    //Required functions and variables

    const [cName, setCustName] = useState("");
    const [cMobNo, setCustMobNo] = useState("");
    const [vehModel, setVehModel] = useState("");
    const [vehType, setVehType] = useState("");
    const [eName, setExeName] = useState("");
    const [eMobNo, setExeMobNo] = useState("");
    const [date, setDate] = useState();
    const [slot, setSlot] = useState("");

    const [testDriveList, setTestDriveList] = useState([]);


    //On page refresh
    useEffect(()=>{
        Axios.get("http://localhost:3005/confirmed").then((response) => {
          setTestDriveList(response);
        });
    }, [])

    return (
        <div>
            <h1>Manager</h1>
            {<ManagerNavBar/>}
            
            <hr></hr>
            
            {vehList.map( (val, key)=> {
                        return <div className='vehicles'>
                                    <h2>{val.model}</h2>
                                    <h2>{val.type}</h2>
                                    <button onClick = {()=> {updVeh(val.vehid)}} >Update this executive</button>  
                                    <button onClick = {()=> {delVeh(val.vehid)}} >Delete this executive</button>
                                </div>
                                
            })}
            
        </div>
    );
}
