import React , {useEffect, useState} from 'react';
import Axios from 'axios';
import {ManagerNavBar} from "../components/ManagerNavBar";

export const Administrate = ()=> {

    //Required functions and variables

    const [exeName, setExeName] = useState("");
    //const [newExeName, updExeName] = useState("");
    const [exeMobNo, setExeMobNo] = useState("");
    //const [newExeMobNo, updExeMobNo] = useState("");
    const [exeList, setExeList] = useState([]);

    const addExe = ()=> {
        if(exeName.length > 0 && exeMobNo.length == 10){
        Axios.post("http://localhost:3005/addExe", 
        {exeName : exeName, exeMobNo : exeMobNo
        }).then(() => {
            setExeList([
            ...exeList,
            {
                name : exeName,
                mobno : exeMobNo
            }
            ]);
        });
        }
        else{
            alert("Invalid data");
        }
    };

    const showExes = ()=>{
        Axios.get("http://localhost:3005/exeDetails").then((response)=> {
            setExeList(response.data);
        });
    };

    const updexe = (id)=>{
        if(exeName.length > 0 && exeMobNo.length == 10){
        Axios.put("http://localhost:3005/updateExe", 
        {exeName : exeName, exeMobNo : exeMobNo, exeId : id
        }).then((response)=> {
            setExeList(exeList.map((val)=>{
                if(val.eid == id){
                    return {eid : id, name : exeName, mobno : exeMobNo};
                }
                else {
                    return val;
                }
            }));
            alert("update success");
            }
        );
        }
        else{
        alert("Invalid data");
        }
    };

    const delexe = (id)=> {
        Axios.delete(`http://localhost:3005/deleteExe/${id}`).then((response)=> {
            setExeList(
            exeList.filter((val)=> {
            if(val.id != id){
                return val;
            }
            })
            );
        });
    };

    //Render page content
    return (
        <div>
            
            <div>
                <h1>Welcome Manager!!</h1>
                {<ManagerNavBar/>}
                <hr></hr>
            </div>

            <div>
                <h1>Manage Executives</h1>
                <div>
                    <label>Executive Name</label>
                    <input 
                        type = "text"
                        onChange = {(event)=> {
                        setExeName(event.target.value);
                        }}
                    />
                    <label>Mobile No</label>
                    <input 
                        type = "text"
                        onChange = {(event)=> {
                        setExeMobNo(event.target.value);
                        }}
                    />
                    <button onClick = {addExe}>Add executive</button>
                    <hr></hr>
                </div>

                <div>
                    <button onClick = {showExes}>Show executives</button>
                        {exeList.map( (val, key)=> {
                        return <div className='executives'>
                                    <h2>{val.name}</h2>
                                    <h2>{val.mobno}</h2>
                                    <button onClick = {()=> {updexe(val.eid)}} >Update this executive</button>  
                                    <button onClick = {()=> {delexe(val.eid)}} >Delete this executive</button>
                                </div>
                                
                        })}
                </div>
            </div>

            
        </div>
    );
}
