import React , {useEffect, useState} from 'react';
import DataTable from 'react-data-table-component';
import Axios from 'axios';
import {CustomerNavBar} from "../components/CustomerNavBar";



export const TrackTestDrive = ()=> {
    //Initialization
    const [userId, setUserId] = useState(0);
    const [userName, setuserName] = useState('');

    const [history, setHistory] = useState([]);
    const [active, setActive] = useState([]);

    Axios.defaults.withCredentials = true;

    
    //Required methods
    useEffect(()=> {
        Axios.get("http://localhost:3005/login").then((response) => {
            console.log("after cookie");
            if(response.data.loggedIn){
                console.log(response);
                setUserId(response.data.user[0].cid);
                setuserName(response.data.user[0].username);
            }
        });
    }, []);

    useEffect(()=> {
        Axios.post("http://localhost:3005/history",{
            id : userId
        }).then((response)=> {
            
            setHistory(response.data);
            console.log(response);
            
            
        });
    }, []);

    useEffect(()=> {
        Axios.post("http://localhost:3005/track",{
            id : userId
        }).then((response)=> {
            if(response.data.length > 0){
            setActive(response.data);
            console.log(response);
            }
            else{
                alert('No active testdrives available');
            }
        });
    }, []);

    const showHistory = ()=>{
        
    };

    const track_td = ()=>{
        
    };

    const Cancel = (id)=> {
        Axios.delete(`http://localhost:3005/cancel/${id}`).then((response)=> {
            if(response.data[0].cancelStatus){
                alert('Cancelled successfully');
            }
            else{
                alert('Cancel unsuccessful');
            }
        });
    };


    //Columns

    const column_history = [
        {
            name : 'model',
            selector : (row) => row.model
        },
        {
            name : 'type',
            selector : (row) => row.type
        },
        {
            name : 'td_date',
            selector : (row) => row.td_date
        },
        {
            name : 'Booking Slot',
            selector : (row) => row.slot_name
        },
        {
            name : 'exe_name',
            selector : (row) => row.exe_name
        }
        ,
        {
            name : 'mobno',
            selector : (row) => row.mobno
        }

    ];

    const column_track = [
        {
            name : 'model',
            selector : (row) => row.model
        },
        {
            name : 'type',
            selector : (row) => row.type
        },
        {
            name : 'td_date',
            selector : (row) => row.td_date
        },
        {
            name : 'slot_name',
            selector : (row) => row.td_slot
        },
        {
            name : 'exe_name',
            selector : (row) => row.exename
        }
        ,
        {
            name : 'mobno',
            selector : (row) => row.exemobno
        },
        {
            name : 'status',
            selector : (row) => row.status
        },
        {
            name : 'cancel-Testdrive',
            cell : (row)=> (
                <button onClick={()=> {Cancel(row.tid)}}>cancel</button>
            )
        }

    ];



    //render page content

    return (
        <div>
            <h1>Hello {userName}</h1>
            {<CustomerNavBar/>}
            <hr></hr>
            <button onClick = {track_td}>Track Active Testdrives</button> <button onClick = {showHistory}>Show Past Testdrives</button>
            <div>
                <h2>Track active testdrive</h2>
                <DataTable
                columns={ column_track}
                data = {active}
                />
            </div>

            <hr></hr><hr></hr>
            <div>
                <h2>Past testdrive</h2>
                <DataTable
                columns={ column_history}
                data = {history}
                />
            </div>
            
        </div>
    );
}
